import React from 'react'
// import NavBar from '../Nav/NavBar'
// import './home.css'

// import { Button } from 'reactstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
// import { faLaughSquint } from '@fortawesome/free-solid-svg-icons'
import { faComment } from '@fortawesome/free-solid-svg-icons'

import { connect } from 'react-redux'
// import { Button } from 'reactstrap'
// import { Link } from 'react-router-dom'
import { getJokeComments } from '../../actions/getJokeComments'

// const laugh = <FontAwesomeIcon icon={faLaughSquint} />
const comment = <FontAwesomeIcon icon={faComment} />

class Joke extends React.Component {
  componentDidMount() {
    this.props.getJokeComments()
  }
  render() {
    return (
      <div key={comment.id} className='indi-comment'>
        <div className=''>
          <img className='profile-pic' src={comment.img_url} alt='profile pic' />
          <h6 className='joke-username'>{comment.username}:</h6>
          <div className='joke-joke'>{comment.comment}</div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
      jokes: state.jokes,
      jokeComments: state.jokeComments,
  }
}

export default connect(
  mapStateToProps,
  { getJokeComments }
)(Joke)
