import React from 'react'
// import NavBar from '../Nav/NavBar'
import { getJokeComments } from '../../actions/getJokeComments'
// import './home.css'

import { connect } from 'react-redux'
// import { Button } from 'reactstrap'
// import { Link } from 'react-router-dom'

class Comments extends React.Component {
  componentDidMount({ joke_id }) {
    this.props.getJokeComments(joke_id)
  }
  render() {
    return (
      <div className='comment-wrapper'>
        <div className='joke-comments'>
        {this.props.jokeComments.map(comment => {
        return (
          <div key={comment.id} className='indi-comment'>
            <div className=''>
              <img src={comment.img_url} alt='profile pic' />
              <h6 className='joke-username'>{comment.username}:</h6>
              <div className='joke-joke'>{comment.comment}</div>
            </div>
          </div>
        )
      })}
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
      // jokes: state.jokes,
      jokeComments: state.jokeComments,
  }
}

export default connect(
  mapStateToProps,
  { getJokeComments }
)(Comments)
