import React from 'react'

// import { Button } from 'reactstrap'
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
// import { faLaughSquint } from '@fortawesome/free-solid-svg-icons'
// import { faComment } from '@fortawesome/free-solid-svg-icons'
import { Link } from 'react-router-dom'

import Joke from './joke'
import { connect } from 'react-redux'

// const laugh = <FontAwesomeIcon icon={faLaughSquint} />
// const comment = <FontAwesomeIcon icon={faComment} />

class Jokes extends React.Component {
  render() {
    const { jokes, toggleReveal, hidden } = this.props
    return (
      <div className='joke-wrapper'>
        {jokes.map(joke => {
          return (
            <Link to={`/jokes/${joke.id}`}>
              <div className='comment-wrapper'>
                <div className='joke-comments'>
                  <div key={this.props.jokes.id} className='indi-joke'>
                    <div className='joke'>
                      <div className='joke-meat'>
                        <img src={this.props.jokes.img_url} alt='profile pic' />
                        <h6 className='joke-username'>{this.props.jokes.username}:</h6>
                        <div className='joke-joke'>{this.props.jokes.joke}</div>
                      </div>
                    </div>
                    <div className='reveal'>
                      <div className='punchline'>
                        <div 
                          id={hidden ? 'hidden' : ''}
                          hidden={hidden}
                          className='joke-punchline'>
                          {this.props.jokes.punchline}
                        </div>
                      </div>
                      <Button>
                        {comment} 5
                      </Button>
                      <Button>{laugh} 56</Button>
                      <Button onClick={toggleReveal}>Reveal Punchline!</Button>
                    </div>
                  </div>
                  <Joke hidden={hidden} toggleReveal={toggleReveal} />
                </div>
              </div>
            </Link>
          )
        })}
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
      jokes: state.jokes,
      jokeComments: state.jokeComments,
  }
}

export default connect(
  mapStateToProps,
  { }
)(Jokes)
