import React from 'react'
import Jokes from './jokes'
import NavBar from '../Nav/NavBar'

import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { getJokes } from '../../actions/getJokes'
import { getJokeComments } from '../../actions/getJokeComments'

class PrivateJokes extends React.Component {
  state = {
    hidden: true,
    joke_id: null
  }

  toggleReveal = this.toggleReveal.bind(this)

  toggleReveal() {
    this.setState({
      hidden: !this.state.hidden
    })
  }

  setJokeId() {
    this.setState({
      joke_id: this.state.jokes.id
    })
  }

  componentDidMount() {
    this.props.getJokes()
    this.props.getJokeComments(joke_id)
  }
  // componentDidMount() {
  //   this.props.getJokeComments({joke_id})
  // }

  render() {
    return (
      <div className='private-jokes-wrapper'>
        <NavBar />
        <Link to='/home'>
          <h2>All jokes component</h2>
        </Link>
        <Jokes 
          jokes={this.props.jokes} 
          toggleReveal={this.toggleReveal} 
          hidden={this.state.hidden}
          jokeComments={this.props.jokeComments}
          joke_id={this.state.joke_id} />
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    jokes: state.jokes,
    jokeComments: state.jokeComments
  }
}

export default connect(
  mapStateToProps,
  { getJokes, getJokeComments }
)(PrivateJokes)
