import React from 'react'

import { Button } from 'reactstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLaughSquint } from '@fortawesome/free-solid-svg-icons'
import { faComment } from '@fortawesome/free-solid-svg-icons'
// import { Link } from 'react-router-dom'

import Comments from './comments'

const laugh = <FontAwesomeIcon icon={faLaughSquint} />
const comment = <FontAwesomeIcon icon={faComment} />

class Jokes extends React.Component {
  componentDidMount() {
    this.props.getJokeComments(jokes.id)
  }
  render() {
    const { jokes, toggleReveal, hidden, jokeComments } = this.props
    return (
      <div className='joke-wrapper'>
        {jokes.map(joke => {
          return (
            <div key={joke.id} className='indi-joke'>
              <div className='joke'>
                <div className='joke-meat'>
                  <img src={joke.img_url} alt='profile pic' />
                  <h6 className='joke-username'>{joke.username}:</h6>
                  <div className='joke-joke'>{joke.joke}</div>
                </div>
              </div>

              <div className='reveal'>
                <div className='punchline'>
                  <div 
                    id={hidden ? 'hidden' : ''}
                    hidden={hidden}
                    className='joke-punchline'>
                    {joke.punchline}
                  </div>
                </div>
                <Button href={Comments} joke_id={joke_id}>
                  {/* <Link to='/comments'> */}
                    {comment} 5
                  {/* </Link> */}
                </Button>
                <Button>{laugh} 56</Button>
                <Button onClick={toggleReveal}>Reveal Punchline!</Button>
              </div>
              <Comments jokeComments={jokeComments} joke_id={joke.id}/>
            </div>
          )
        })}
      </div>
    )
  }
}

export default Jokes
