// import { connect } from 'react-redux'
import { Button } from 'reactstrap'
// import { Link } from 'react-router-dom'
import { getJokeComments } from '../../actions/getJokeComments'

const laugh = <FontAwesomeIcon icon={faLaughSquint} />
const comment = <FontAwesomeIcon icon={faComment} />

class Comments extends React.Component {

  render() {
    console.log(`Joke ID: ${this.props.jokeComments.joke_id}`)
    return (
      <div className='comment-wrapper'>
        <div className='joke-comments'>
        {this.props.jokeComments.map(comment => {
          return (
            <div key={joke.id} className='indi-joke'>
              <div className='joke'>
                <div className='joke-meat'>
                  <img src={joke.img_url} alt='profile pic' />
                  <h6 className='joke-username'>{joke.username}:</h6>
                  <div className='joke-joke'>{joke.joke}</div>
                </div>
              </div>

              <div className='reveal'>
                <div className='punchline'>
                  <div 
                    id={hidden ? 'hidden' : ''}
                    hidden={hidden}
                    className='joke-punchline'>
                    {joke.punchline}
                  </div>
                </div>
                <Button>
                  {/* <Link to='/comments'> */}
                    {comment} 5
                  {/* </Link> */}
                </Button>
                <Button>{laugh} 56</Button>
                <Button onClick={toggleReveal}>Reveal Punchline!</Button>
              </div>
            </div>
            // <div key={comment.id} className='indi-comment'>
            //   <div className=''>
            //     <img className='profile-pic' src={comment.img_url} alt='profile pic' />
            //     <h6 className='joke-username'>{comment.username}:</h6>
            //     <div className='joke-joke'>{comment.comment}</div>
            //   </div>
            // </div>
            )
          })}
        </div>
      </div>
    )
  }
}

export default Joke
