import React from 'react'
// import './update.css'

import { connect } from 'react-redux'
import { getJokeById } from '../../actions/getJokeById'
import { updateJoke } from '../../actions/updateJoke'
import { deleteJoke } from '../../actions/deleteJoke'
import { Form, Input, Button } from 'reactstrap'
import { Link } from 'react-router-dom'

class UpdateJoke extends React.Component {
  constructor({joke}) {
    super({joke})
    this.state = {
      updatedJoke: {
        id: `${joke.id}`,
        joke: `${joke.joke}`,
        punchline: `${joke.punchline}`,
        // revealed: `${this.props.joke.revealed}`,
        // laughs: `${this.props.joke.laughs}`
      }
    }
  }

  componentDidMount() {
    // this.props.getJokeComments(this.props.match.params.id)
    this.props.getJokeById(this.props.match.params.id)
  }

  handleInputChange = e => {
    this.setState({
      updatedJoke: {
        ...this.state.updatedJoke,
        [e.target.name]: e.target.value
      }
    })
  }

  handleSubmit = e => {
    e.preventDefault()
    this.props
      .updateJoke(this.state.updatedJoke)
      .then(res => (this.props.history.push('/home')))
    // console.log(this.props.user.id)
  }

  render() {
    // const {  joke } = this.props
    // console.log(joke)
    return (
      <div className='update_form_wrapper'>
        { console.log(this.state.updatedJoke) }
        { console.log(this.props.joke) }
        <Link to='/home'>
          <h2>Update joke form</h2>
        </Link>
        <Form onSubmit={this.handleSubmit}>
          <div className='key_value_pears'> {/* üîëüçê */}
            <h5>Joke:</h5>
            <Input 
              type='text'
              name='joke'
              placeholder='Joke'
              value={this.state.updatedJoke.joke}
              onChange={this.handleInputChange}
            />
          </div>

          <div className='key_value_pears'> {/* üîëüçê */}
            <h5>Punchline:</h5>
            <Input 
              type='text'
              name='punchline'
              placeholder='Punchline'
              value={this.state.updatedJoke.punchline}
              onChange={this.handleInputChange}
            />
          </div>

          {/* <div className='key_value_pears'>
            <h5>Revealed:</h5>
            <Input 
              type='text'
              name='revealed'
              placeholder='Revealed'
              value={this.state.updatedJoke.revealed}
              onChange={this.handleInputChange}
            />
          </div>

          <div className='key_value_pears'>
            <h5>Laughs:</h5>
            <Input 
              type='text'
              name='laughs'
              placeholder='Profile image URL'
              value={this.state.updatedJoke.laughs}
              onChange={this.handleInputChange}
            />
          </div> */}

          <Button>Submit Changes</Button>
        </Form>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    joke: state.joke,
  }
}

export default connect(
  mapStateToProps,
  { updateJoke, deleteJoke, getJokeById }
)(UpdateJoke)
